import React from "react";

function App() {
let cDate = new Date();
cDate = cDate.getHours();
let greeting = '';
let colorChange = { };
if(cDate >=1 && cDate < 12)
{
    greeting = 'Good Morning';
    colorChange.color = "sea";
}
else if(cDate >=12 && cDate < 20)
{
    greeting = 'Good Afternoon';
    colorChange.color = "orange";
}
else if(cDate >= 20 && cDate < 24)
{
    greeting = 'Good Night';
    colorChange.color = "purple";
}

return ( 
    <>
<div>
    <h1>
    Hello Sir, <span style = {colorChange}>{greeting}</span>
    </h1>
    </div>
    </>
    );

}

export default App;
